// Init the game
function init() {

    seconds.innerHTML = currentLevel;
    showWord(words);
    setInterval(countdown, 1000);
    wordInput.addEventListener('input', startGame);
    setInterval(checkStatus, 50);
    buttonEasy.addEventListener('click', function () {
        currentLevel = levels.Easy;
        time = currentLevel;
        score = 0;
        showWord(words);
        startGame()
    });
    buttonMedium.addEventListener('click', function () {
        currentLevel = levels.Medium;
        time = currentLevel;
        score = 0;
        showWord(words);
        startGame()
    });
    buttonDiablo.addEventListener('click', function () {
        currentLevel = levels.Diablo;
        time = currentLevel;
        score = 0;
        showWord(words);
        startGame()
    });
    highScoreEasy.innerHTML = highscoreEasy;
    highScoreMedium.innerHTML = highscoreMedium;
    highScoreDiablo.innerHTML = highscoreDiablo;
}

function showWord(words) {

    if (currentLevel.valueOf() === levels.Diablo) {
        currentWord.innerHTML = createRandomWord();
    } else {
        const randomIndex = Math.floor(Math.random() * words.length);
        currentWord.innerHTML = words[randomIndex];
    }
}

function countdown() {
    if (time > 0) {
        time--;
    } else if (time === 0) {
        isPlaying = false;
    }
    timeDisplay.innerHTML = time;
}

function checkStatus() {
    if (!isPlaying && time === 0) {
        message.classList.remove('text-success');
        message.classList.add('text-danger');
        message.innerHTML = 'Game Over';
        score = -1;
    }
}

function startGame() {

    seconds.innerHTML = currentLevel;

    if (matchWords()) {
        isPlaying = true;
        time = currentLevel + 1;
        showWord(words);
        wordInput.value = '';
        score++;
    }
    if (currentLevel === levels.Easy && score > highscoreEasy) {
        highscoreEasy = score;
        localStorage.setItem('highscoreEasy', score);
    }
    if (currentLevel === levels.Medium && score > highscoreMedium) {
        highscoreMedium = score;
        localStorage.setItem('highscoreMedium', score);
    }
    if (currentLevel === levels.Diablo && score > highscoreDiablo) {
        highscoreDiablo = score;
        localStorage.setItem('highscoreDiablo', score);
    }
    if (score === -1) {
        scoreDisplay.innerHTML = '0';
    } else {
        scoreDisplay.innerHTML = score;
        highScoreEasy.innerHTML = highscoreEasy;
        highScoreMedium.innerHTML = highscoreMedium;
        highScoreDiablo.innerHTML = highscoreDiablo;

    }
}

function matchWords() {
    if (wordInput.value === currentWord.innerHTML) {
        message.classList.remove('text-danger');
        message.classList.add('text-success');
        message.innerHTML = 'Correct!';
        return true
    } else {
        message.innerHTML = '';
        return false
    }
}


function createRandomWord() {

    let text = "";
    const possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()-_+`~[{]}|<,.>?/;";

    for (let i = 0; i < 5; i++)
        text += possible.charAt(Math.floor(Math.random() * possible.length));

    return text;
}
